INSERT INTO public.type (type_name, type_description)
VALUES
('Kháng sinh', 'Kháng sinh là nhóm thuốc có khả năng tiêu diệt hoặc ức chế sự phát triển của vi khuẩn, dùng để điều trị các bệnh nhiễm trùng. '),
('Thực phẩm chức năng', 'Là sản phẩm được thiết kế để bổ sung vào chế độ ăn uống hàng ngày, cung cấp các dưỡng chất hoặc tác động có lợi cho sức khỏe. Các thực phẩm chức năng không thay thế được thuốc chữa bệnh nhưng có thể hỗ trợ tăng cường sức khỏe, cải thiện chức năng cơ thể, giúp phòng ngừa bệnh tật hoặc hỗ trợ điều trị các tình trạng sức khỏe nhất định.'),
('Thuốc giảm đau - hạ sốt', 'Thuốc giảm đau - hạ sốt dùng để làm giảm đau và hạ nhiệt độ cơ thể khi có sốt hoặc đau nhẹ.'),
('Thuốc chống viêm', 'Thuốc chống viêm được dùng để giảm viêm, sưng và đau do các bệnh lý viêm.'),
('Thuốc điều trị tim mạch', 'Thuốc điều trị tim mạch giúp kiểm soát huyết áp, rối loạn nhịp tim, và các bệnh lý về tim mạch.'),
('Thuốc điều trị tiêu hóa', 'Thuốc điều trị tiêu hóa hỗ trợ trong việc điều trị các bệnh lý liên quan đến dạ dày, ruột và hệ tiêu hóa.'),
('Thuốc kháng histamin (Chống dị ứng)', 'Thuốc kháng histamin giúp giảm các triệu chứng dị ứng như ngứa, sổ mũi, và mề đay.'),
('Thuốc điều trị nội tiết', ' Thuốc điều trị nội tiết can thiệp vào các chức năng cơ thể liên quan đến hormone.'),
('Thuốc điều trị mắt, tai, mũi', 'Là các loại thuốc được sử dụng để điều trị các vấn đề về mắt, tai và mũi, bao gồm các bệnh lý nhiễm trùng, viêm, dị ứng, hoặc các vấn đề khác liên quan đến các cơ quan này.'),
('Thuốc Đông dược', 'Là các loại thuốc được chiết xuất từ nguyên liệu thiên nhiên, chủ yếu là các loại cây cỏ, thảo mộc, khoáng chất, và động vật. Thuốc Đông dược có nguồn gốc từ nền y học cổ truyền phương Đông, đặc biệt là Trung Quốc và Việt Nam, với mục tiêu cân bằng cơ thể và chữa trị bệnh tật bằng cách sử dụng các thành phần tự nhiên.'),
('Thuốc Tiêm - Truyền', 'Là các loại thuốc được đưa vào cơ thể thông qua hình thức tiêm (chích trực tiếp vào cơ thể) hoặc truyền (dung dịch thuốc được đưa vào cơ thể qua đường truyền tĩnh mạch).'),
('Thuốc Tim Mạch', 'Là các loại thuốc được sử dụng để điều trị các bệnh lý liên quan đến tim và mạch máu, bao gồm các rối loạn về huyết áp, nhịp tim, bệnh mạch vành, suy tim, đột quỵ, và các bệnh lý khác liên quan đến hệ tim mạch.'),
('Vật Tư Y Tế', 'Là các thiết bị, dụng cụ, sản phẩm hoặc vật liệu được sử dụng trong chăm sóc sức khỏe, điều trị bệnh, chẩn đoán và phục hồi chức năng. Chúng giúp bác sĩ và nhân viên y tế thực hiện các thủ thuật, xét nghiệm, phẫu thuật và chăm sóc bệnh nhân.'),
('Khác', 'Các nhóm thuốc khác');
